import React, { useEffect } from "react";
import Prism from "prismjs";
import "prismjs/themes/prism-tomorrow.css";
import styles from "./Help.module.scss";
import "prismjs/components/prism-csharp";
import "prismjs/components/prism-javascript";
import "prismjs/components/prism-java";
import "prismjs/components/prism-python";
import "prismjs/components/prism-go";
import "prismjs/components/prism-c";
import "prismjs/components/prism-cpp";
import "prismjs/components/prism-markup";

const Help = () => {
  const html = `<p><strong>1.&nbsp;</strong><strong>C&aacute;ch nộp b&agrave;i tr&ecirc;n hệ thống</strong></p>\r\n<p>Hệ thống sẽ chấm điểm dựa tr&ecirc;n gi&aacute; trị trả về của h&agrave;m cho trước, đầu v&agrave;o v&agrave; đầu ra ch&iacute;nh l&agrave; c&aacute;c tham số v&agrave; gi&aacute; trị trả về của h&agrave;m. Do đ&oacute;, bạn chỉ cần ho&agrave;n thiện h&agrave;m cho trước, kh&ocirc;ng cần phải thực hiện nhập xuất dữ liệu. V&iacute; dụ nếu b&agrave;i tập y&ecirc;u cầu viết h&agrave;m trả về tổng 2 số nguy&ecirc;n th&igrave; bạn c&oacute; thể l&agrave;m như sau:</p>\r\n<p>Với ng&ocirc;n ngữ<span>&nbsp;</span><strong>C++, Java, C#</strong>:</p>\r\n<pre class="language-cpp"><code>int add(int a, int b){\r\n    return a + b;\r\n}</code></pre>\r\n<p>Với ng&ocirc;n ngữ<span>&nbsp;</span><strong>Javascript</strong>:</p>\r\n<pre class="language-javascript"><code>function add(a, b){\r\n    return a + b;\r\n}</code></pre>\r\n<p>Với ng&ocirc;n ngữ<span>&nbsp;</span><strong>Python</strong>:</p>\r\n<pre class="language-python"><code>def add(a, b):\r\n    return a + b</code></pre>\r\n<p>Với ng&ocirc;n ngữ<span>&nbsp;</span><strong>Go</strong>:</p>\r\n<pre class="language-markup"><code>func add(a int, b int) int {\r\n    return a + b;\r\n}</code></pre>\r\n<p><strong>Th&ocirc;ng tin về c&aacute;c tr&igrave;nh dịch tr&ecirc;n hệ thống</strong></p>\r\n<ul>\r\n<li>java: jdk8.0.24</li>\r\n<li>python2: Python 2.7.16</li>\r\n<li>python3: Python 3.7.78</li>\r\n<li>C++: gcc version 8.3.0</li>\r\n<li>C#:&nbsp;Mono JIT compiler version 6.8.0.96</li>\r\n<li>Javascript:&nbsp;Node.js v13.8.0</li>\r\n<li>Go: go1.13.8</li>\r\n</ul>\r\n<p><strong>2. C&aacute;c thư viện đ&atilde; được hệ thống import sẵn</strong></p>\r\n<div>Ng&ocirc;n ngữ lập tr&igrave;nh C:</div>\r\n<div>\r\n<pre class="language-c"><code>#include &lt;stdio.h&gt;\r\n#include &lt;stdlib.h&gt;\r\n#include &lt;string.h&gt;\r\n#include &lt;time.h&gt;\r\n#include &lt;errno.h&gt;\r\n#include &lt;math.h&gt;\r\n#include &lt;ctype.h&gt;</code></pre>\r\n</div>\r\n<div>Ng&ocirc;n ngữ lập tr&igrave;nh C++:</div>\r\n<div>\r\n<pre class="language-cpp"><code>#include &lt;iostream&gt;\r\n#include &lt;string&gt;\r\n#include &lt;vector&gt;\r\n#include &lt;errno.h&gt;\r\n#include &lt;string.h&gt;\r\n#include &lt;stdlib.h&gt;\r\n#include &lt;unistd.h&gt;\r\n#include &lt;netdb.h&gt;\r\n#include &lt;fstream&gt;\r\n#include &lt;chrono&gt;\r\n#include &lt;iomanip&gt;\r\n#include &lt;cstdarg&gt;\r\n#include &lt;cstdio&gt;\r\n#include &lt;cctype&gt;\r\n#include &lt;cmath&gt;\r\n#include &lt;queue&gt;\r\n#include &lt;map&gt;\r\n#include &lt;set&gt;\r\n#include &lt;algorithm&gt;\r\n#include &lt;climits&gt;\r\n#include &lt;sstream&gt;\r\n#include &lt;numeric&gt;\r\n#include &lt;iterator&gt;\r\n#include &lt;utility&gt;\r\n#include &lt;stack&gt;\r\n#include &lt;functional&gt;\r\n#include &lt;deque&gt;\r\n#include &lt;complex&gt;\r\n#include &lt;bitset&gt;\r\n#include &lt;list&gt;\r\n#include &lt;array&gt;\r\n#include &lt;regex&gt;\r\n#include &lt;random&gt;\r\n#include &lt;unordered_set&gt;\r\n#include &lt;unordered_map&gt;\r\n#include &lt;cstdlib&gt;\r\n#include &lt;typeinfo&gt;\r\n#include &lt;bits/stdc++.h&gt;</code></pre>\r\n</div>\r\n<div>Ng&ocirc;n ngữ lập tr&igrave;nh C#:</div>\r\n<div>\r\n<pre class="language-csharp"><code>using System;\r\nusing System.IO;\r\nusing System.Collections;\r\nusing System.Collections.Generic;\r\nusing System.ComponentModel;\r\nusing System.Globalization;\r\nusing System.Runtime.Serialization;\r\nusing System.Text;\r\nusing System.Linq;\r\nusing System.Xml.Linq;\r\nusing System.Xml;\r\nusing System.Data;\r\nusing System.Web;\r\nusing System.Web.Helpers;\r\nusing System.Text.RegularExpressions;</code></pre>\r\n</div>\r\n<div>Ng&ocirc;n ngữ lập tr&igrave;nh Go:</div>\r\n<div>\r\n<pre class="language-markup"><code>Go:\r\nimport (\r\n    "os"\r\n    "fmt"\r\n    "time"\r\n    "io/ioutil"\r\n    "encoding/json"\r\n    "math"\r\n    "strconv"\r\n    "strings"\r\n    "sort"\r\n)</code></pre>\r\n</div>\r\n<div>Ng&ocirc;n ngữ lập tr&igrave;nh Java:</div>\r\n<div>\r\n<div>\r\n<pre class="language-java"><code>import java.io.*;\r\nimport java.util.*;\r\nimport java.math.*;\r\nimport java.util.regex.*;\r\nimport java.util.stream.*;\r\nimport java.text.*;\r\nimport java.util.function.*;</code></pre>\r\n</div>\r\n<div>Ng&ocirc;n ngữ lập tr&igrave;nh Python:</div>\r\n<div>\r\n<pre class="language-python"><code>import decimal\r\nimport math\r\nimport string\r\nimport re\r\nimport random\r\nimport functools</code></pre>\r\n</div>\r\n</div>\r\n<p><strong>&nbsp;3.&nbsp;</strong><b>Hướng dẫn sử dụng kiểu dữ liệu mảng v&agrave; ma trận đối với ng&ocirc;n ngữ lập tr&igrave;nh C</b><br />C&aacute;c kiểu dữ liệu c&oacute; t&ecirc;n&nbsp;<code>arr_##name, matrix_##name</code> l&agrave; c&aacute;c kiểu dữ liệu đ&atilde; được định nghĩa sẵn v&agrave; c&oacute; cấu tr&uacute;c giống như sau:</p>\r\n<pre class="language-c"><code>typedef struct arr_bool {\r\n\tint* arr;\r\n\tint size;\r\n} arr_bool;\r\n\r\ntypedef struct matrix_bool {\r\n\tarr_bool* arr;\r\n\tint size;\r\n} matrix_bool;\r\n\r\ntypedef struct arr_char{\r\n    char* arr;\r\n    int size;\r\n} arr_char;\r\n\r\ntypedef struct arr_string {\r\n\tchar** arr;\r\n\tint size;\r\n} arr_string;\r\n\r\ntypedef struct arr_int {\r\n\tint* arr;\r\n\tint size;\r\n} arr_int;\r\n\r\ntypedef struct arr_long {\r\n\tlong long* arr;\r\n\tint size;\r\n} arr_long;\r\n\r\ntypedef struct arr_double {\r\n\tdouble* arr;\r\n\tint size;\r\n} arr_double;\r\n\r\ntypedef struct matrix_char {\r\n\tarr_char* arr;\r\n\tint size;\r\n} matrix_char;\r\n\r\ntypedef struct matrix_string {\r\n\tarr_string* arr;\r\n\tint size;\r\n} matrix_string;\r\n\r\ntypedef struct matrix_int {\r\n\tarr_int* arr;\r\n\tint size;\r\n} matrix_int;\r\n\r\ntypedef struct matrix_long {\r\n\tarr_long* arr;\r\n\tint size;\r\n} matrix_long;\r\n\r\ntypedef struct matrix_double {\r\n\tarr_double* arr;\r\n\tint size;\r\n} matrix_double;\r\n</code></pre>\r\n<p>V&iacute; dụ nếu b&agrave;i tập y&ecirc;u cầu bạn t&iacute;nh tổng c&aacute;c số trong mảng <code>arr</code> th&igrave; bạn c&oacute; thể l&agrave;m như sau:</p>\r\n<pre class="language-c"><code>int sumOfArray(arr_int arr){\r\n    int sum = 0;\r\n    for(int i = 0; i &lt; arr.size; ++i){\r\n        sum += arr.arr[i];\r\n    }\r\n    return sum;\r\n}</code></pre>\r\n<p>V&iacute; dụ kh&aacute;c, nếu b&agrave;i tập y&ecirc;u cầu viết h&agrave;m trả về mảng c&aacute;c x&acirc;u c&oacute; độ d&agrave;i lớn hơn 3 trong mảng cho trước th&igrave; bạn c&oacute; thể l&agrave;m giống như sau:</p>\r\n<pre class="language-c"><code>arr_string getArray(arr_string input) {\r\n    int size = 0;\r\n    arr_string result;\r\n    result.arr = (char**)malloc(input.size * sizeof(char*));\r\n    for (int i = 0; i &lt; input.size; i++) {\r\n        if (strlen(input.arr[i]) &gt;= 3) {\r\n            result.arr[size++] = input.arr[i];\r\n        }\r\n    }\r\n    result.size = size;\r\n    return result;\r\n}</code></pre>\r\n<p>Lưu &yacute;: hệ thống sẽ chấm điểm dựa tr&ecirc;n thuộc t&iacute;nh&nbsp;<code>size</code> v&agrave; c&aacute;c phần tử c&oacute; chỉ số từ <code>0 -&gt; size - 1</code> trong thuộc t&iacute;nh&nbsp;<code>arr</code>.</p>\r\n<p>Tương tự, nếu b&agrave;i tập y&ecirc;u cầu viết h&agrave;m trả về ma trận c&aacute;c số từ <code>1</code> tới <code>9</code> th&igrave; bạn c&oacute; thể l&agrave;m giống như sau:</p>\r\n<pre class="language-c"><code>matrix_int getMatrix() {\r\n    matrix_int matrix;\r\n    matrix.arr = (arr_int*)malloc(3 * sizeof(arr_int));\r\n    for (int i = 0; i &lt; 3; ++i) {\r\n        matrix.arr[i].size = 3;\r\n        matrix.arr[i].arr = (int*)malloc(3 * sizeof(int));\r\n        for (int j = 0; j &lt; 3; ++j) {\r\n            matrix.arr[i].arr[j] = i * 3 + j + 1;\r\n        }\r\n    }\r\n    return matrix;\r\n}</code></pre>\r\n<p><strong>4. C&oacute; bao nhi&ecirc;u kiểm thử ẩn trong một b&agrave;i tập?</strong></p>\r\n<p><strong>-&nbsp;</strong>Số kiểm thử ẩn sẽ được quy ước theo c&ocirc;ng thức sau:<br /><img src="data:image/png;base64," /></p>\r\n<p><em>(kết quả được l&agrave;m tr&ograve;n xuống nếu số kiểm thử l&agrave; số lẻ)</em></p>\r\n<p><strong>5.&nbsp;Quy tắc chấm điểm ở mỗi b&agrave;i tập l&agrave; như thế n&agrave;o?<br /></strong>- Trường hợp số c&acirc;u hỏi sai l&agrave; 1 th&igrave; người học sẽ được 1/2 số điểm tối đa của b&agrave;i tập đ&oacute;<br />- Trường hợp số c&acirc;u hỏi sai l&agrave; 2 th&igrave; người học sẽ được 1/4 số điểm tối đa của b&agrave;i tập đ&oacute;<br />- Trường hợp số c&acirc;u hỏi sai lớn hơn 2 th&igrave; người học sẽ kh&ocirc;ng được điểm n&agrave;o ở b&agrave;i tập đ&oacute;</p>\r\n<p><strong>6.&nbsp;Tại sao t&ocirc;i kh&ocirc;ng c&oacute; nh&igrave;n thấy tab Lịch sử nộp v&agrave; tab B&igrave;nh luận?</strong></p>\r\n<p>Bạn kh&ocirc;ng nh&igrave;n thấy th&ocirc;ng tin n&agrave;y do chưa đăng nhập hệ thống, ở g&oacute;c b&ecirc;n phải cuối m&agrave;n h&igrave;nh sẽ hiển n&uacute;t đăng nhập để bạn thực hiện thao t&aacute;c đăng nhập hệ thống</p>\r\n<p><img width="1044" height="310" alt="" src="${process.env.NEXT_PUBLIC_CDN_URL}/Media/Default/help/c&acirc;u%20hỏi%203%20trong%20FAQ%20task%20tiếng%20Việt.png" /></p>\r\n<p><strong>7.&nbsp;Tại sao t&ocirc;i kh&ocirc;ng thấy hiển thị c&oacute; n&uacute;t nộp b&agrave;i?</strong></p>\r\n<p>Kh&ocirc;ng hiển thị n&uacute;t nộp b&agrave;i c&oacute; thể bạn đang ở 1 trong những trường hợp sau:<br />- Chưa thực hiện đăng nhập t&agrave;i khoản v&agrave;o hệ thống<br />- Chưa đăng k&iacute; thi v&agrave;o trong cuộc thi<br />- Cuộc thi đang mở đ&atilde; hết thời gian thi<br />- B&agrave;i tập bạn đang mở đ&atilde; sử dụng hết lượt nộp b&agrave;i ( mỗi b&agrave;i sẽ c&oacute; khoảng 10 lượt nộp b&agrave;i)</p>\r\n<p><strong>8.&nbsp;Số đi k&egrave;m theo n&uacute;t "Nộp b&agrave;i" c&oacute; &yacute; nghĩa g&igrave;?&nbsp;<img src="data:image/png;base64," /></strong></p>\r\n<p>Đ&acirc;y l&agrave; tổng số lươt nộp b&agrave;i của b&agrave;i tập đ&oacute;, số lần được tự trừ đi sau mỗi lần người d&ugrave;ng nộp b&agrave;i</p>\r\n<p><strong>9.&nbsp;Giới hạn k&yacute; tự c&oacute; &yacute; nghĩa g&igrave;?</strong></p>\r\n<p>L&agrave; giới hạn số k&iacute; tự d&ugrave;ng để giải quyết b&agrave;i to&aacute;n kh&ocirc;ng t&iacute;nh k&iacute; tự khoảng trắng v&agrave; xuống d&ograve;ng</p>\r\n<p><strong>10. Giới hạn thời gian chạy một testcase l&agrave; g&igrave;?</strong></p>\r\n<p>Mỗi ng&ocirc;n ngữ sẽ c&oacute; một giới hạn thời gian chạy kh&aacute;c nhau&nbsp;</p>\r\n<p>Giới hạn thời gian chạy testcase l&agrave; thời gian tối đa để đoạn m&atilde; nguồn m&agrave; người d&ugrave;ng nộp l&ecirc;n hệ thống d&ugrave;ng để xử l&yacute; 1 testcase. Trường hợp thời gian chạy thực tế lớn hơn giới hạn thời gian của test case đ&oacute; th&igrave; testcase đ&oacute; sẽ bị b&aacute;o Fail</p>\r\n<p><strong>11. T&ocirc;i muốn xem lại b&agrave;i m&igrave;nh đ&atilde; nộp th&igrave; như thế n&agrave;o?</strong></p>\r\n<p>Bạn mở phần "Lịch sử nộp" của m&igrave;nh để c&oacute; thể xem lại to&agrave;n bộ những lượt nộp b&agrave;i của m&igrave;nh v&agrave; chọn từng lượt nộp sẽ hiển thị lại đoạn m&atilde; nguồn bạn đ&atilde; ghi nhận</p>\r\n<p><img width="846" height="374" alt="" src="${process.env.NEXT_PUBLIC_CDN_URL}/Media/Default/help/help_10.png" /></p>`;

  useEffect(() => {
    Prism.highlightAll();
  }, []);

  return (
    <div className={styles.content}>
      <div dangerouslySetInnerHTML={{ __html: html }}></div>
    </div>
  );
};

export default Help;
